children.sourceData:
{{
  (() => {
    // Get the selected VLAN ID
    const selectedVlanId = DeleteVlanDropdown.selectedOptionValue;

    // If no VLAN selected, show all switches
    if (!selectedVlanId) {
      return getSwitches.data.map(s => ({label: s.ip, value: s.ip}));
    }

    // Find the selected VLAN in the VLAN list
    const selectedVlan = getVlans.data.find(v => v.vlan_id === selectedVlanId);

    // If VLAN not found or has no switches, return empty
    if (!selectedVlan || !selectedVlan.switches) {
      return [];
    }

    // Parse the comma-separated switch IPs
    const vlanSwitchIps = selectedVlan.switches.split(',');

    // Filter switches based on the VLAN list
    return getSwitches.data.filter(s => vlanSwitchIps.includes(s.ip))
      .map(s => ({label: s.ip, value: s.ip}));
  })()
}}

