customColumn1.onClick:
{{
  (async () => {
    const ip = switchport_table.triggeredRow.switch_ip;
    const port = switchport_table.triggeredRow.port_id;

    if (!ip || !port) { showAlert("Missing IP or port", "error"); return; }

    await storeValue('portcfg_ip', ip);
    await storeValue('portcfg_port', port);

    // Run the API
    const res = await get_port_config.run();

    // If backend returned JSON (e.g., auth required), handle it
    if (typeof res === "object" && res !== null) {
      if (res.requires_credentials) {
        showAlert("Credentials required. Please enter global credentials.", "warning");
        showModal("CredentialsModal"); // your existing creds modal
        return;
      }
      if (res.ok === false) {
        showAlert(res.message || "Failed to fetch port config", "error");
        return;
      }
    }

    // Extract plain text
    const text = (typeof res === "string")? res
      : (res?.response ?? res?.content ?? "");

    PortConfigTitle.setText(`${ip} - Interface ${port}`);
 PortConfigText.setText(text || "(no output)");
    showModal(PortConfigModal.name);
  })()
}}

