children.isDisabled:
{{
  // 1. Error Guard: Check if the state is ready. If not, disable the button (TRUE).
  appsmith.store.originalPortValues === undefined || appsmith.store.originalPortValues.desc === undefined

  ? true 

  // 2. Comparison Logic: Check if ANY field has changed (returns TRUE if edited).
  : ! (
      // Text Input: Check if current value is NOT equal to original
      (desc.text !== appsmith.store.originalPortValues.desc) ||

      // Dropdowns/Selects: Check if current selected value is NOT equal to original
      (adminState.selectedOptionValue !== appsmith.store.originalPortValues.adminState) ||
      (portMode.selectedOptionValue !== appsmith.store.originalPortValues.portMode) ||
      (accessVLAN.selectedOptionValue !== appsmith.store.originalPortValues.accessVLAN) ||
      (nativeVLAN.selectedOptionValue !== appsmith.store.originalPortValues.nativeVLAN) ||
      (speed.selectedOptionValue !== appsmith.store.originalPortValues.speed) ||
      (duplex.selectedOptionValue !== appsmith.store.originalPortValues.duplex) ||
      (poeStatus.selectedOptionValue !== appsmith.store.originalPortValues.poeStatus) ||

(ipv4Acl.isChecked !== appsmith.store.originalPortValues.ipv4Acl) ||
      (ipv6Acl.isChecked !== appsmith.store.originalPortValues.ipv6Acl) ||

      // Checkbox: Check if current state is NOT equal to original
      (dot1x.isChecked !== appsmith.store.originalPortValues.dot1x) ||

      // Multi-Select Arrays: Use JSON.stringify for deep comparison
      (JSON.stringify(taggedVlans.selectedOptionValues) !== JSON.stringify(appsmith.store.originalPortValues.taggedVlans)) || 
      (JSON.stringify(stp.selectedOptionValues) !== JSON.stringify(appsmith.store.originalPortValues.stp))
    )
}}

