children.onClick:
{{
lldpNaming.run().then(() => {
    const res = lldpNaming.data;
    const msg = res?.message?.toLowerCase() || "";
    const stats = res?.statistics;

    //
    // 1 Authentication required
    //
    if (res?.requires_credentials) {
      showAlert("🔐 Authentication Required: Please set global credentials first", "warning");
      return;
    }

    //
    // 2 No ports found
    //
    if (msg.includes("no ports found")) {
      showAlert("📭 No ports found matching the criteria (e.g., VLAN 100/101 + LLDP).", "info");
      return;
    }

    //
    // 3 No online switches
    //
    if (msg.includes("no online switches")) {
      showAlert("📴 No online switches found. Check connectivity.", "warning");
      return;
    }

    //
    // 4 Explicit "no updates needed"
    //
    if (msg.includes("no updates") || msg.includes("already have correct descriptions")) {
      showAlert(` ${res.message}`, "info");
      return;
    }

    //
    // 5 If backend returned statistics, classify based on numbers
    //
    if (stats) {
      const total = stats.total ?? 0;
      const success = stats.successful ?? 0;
      const failed = stats.failed ?? 0;

      // 💯 All failed
      if (failed === total && total > 0) {
        showAlert(` All ${total} updates failed. Check connectivity or credentials.`, "error");
        return;
      }

      // 🔶 Partial success
      if (failed > 0 && success > 0) {
        showAlert(` Partial success: ${success} updated, ${failed} failed.`, "warning");
        return;
      }

      // 🎉 Full success
      if (success === total && total > 0) {
        showAlert(` All ${total} ports updated successfully.`, "success");
        return;
      }
    }

    //
    // 6 Fallback: if message contains "successful" treat as success
    //
    if (msg.includes("successful")) {
      showAlert(` ${res.message}`, "success");
      return;
    }

    //
    // 7 Last fallback  real error or unknown msg
    //
    if (res?.message) {
      showAlert(` Error: ${res.message}`, "error");
      return;
    }

    // 8 True unknown error
    showAlert(" Unknown error occurred. Please try again.", "error");
});
}}

