{{(() => {
  if (!currentItem?.id) {
    showAlert("Cannot delete unsaved rule", "error");
    return;
  }

    DeleteRuleAPI.run(
      () => {
        showAlert("Rule deleted successfully", "success");

        // Refresh from database
        GetRulesAPI.run(() => {
          // After API loads, update store
          const apiRules = GetRulesAPI.data?.rules || [];
          storeValue('rulesList', apiRules);
          storeValue('_refresh', Date.now());
        });
      },
