customColumn3.onClick:
{{
  (async () => {
    // 1) Resolve the IP from the row that fired the action
    const ip =
      DiscoveredSwitchesTable?.triggeredRow?.ip ||
      (typeof currentRow !== "undefined" ? currentRow.ip : undefined) ||
      DiscoveredSwitchesTable?.selectedRow?.ip;

    await storeValue('target_ip', ip);

    // 2) Call the API (POST /download_config/). If your query body uses store, no args needed.
    const res = await download_config.run();

    // 3) Normalize response to plain text
    const raw = res ?? download_config.data;
    const txt =
      typeof raw === "string"
        ? raw
        : Array.isArray(raw)
          ? raw[0]?.response
          : (raw?.response ?? raw?.content ?? "");

    // 4) Guard + download
    if (!ip || !txt) {
      showAlert("Failed to download configuration. Missing content or switch IP.", "error");
      return "";
    }

    // 5) timestamped filename
    const z = n => String(n).padStart(2, "0");
    const d = new Date();
    const stamp = `${z(d.getDate())}-${z(d.getMonth()+1)}-${d.getFullYear()}_${z(d.getHours())}-${z(d.getMinutes())}-${z(d.getSeconds())}`;
    const safe = String(ip).replace(/[^\w.\-]+/g, "_");

    download(txt, `${safe}_${stamp}.txt`, "text/plain");
    return "";
  })()
}}

