        hasAnyUplink = true;
      }
      if (key === 'is_dot1x') {
        if (currentValue === true) {
          hasAnyDot1xOn = true;
        } else {
          hasAnyDot1xOff = true;
        }
      }
      if (currentValue !== firstValue) {
        isIdentical = false;
      }
      portIds.push(rows[i].port_id);
    }
    defaults[key] = isIdentical ? commonValue : null;
  }
  defaults.port_ids = Array.from(new Set(portIds)).join(', ');
  defaults.is_all_uplink = defaults.is_uplink === true;
  defaults.has_any_uplink = hasAnyUplink;
  defaults.is_mixed_dot1x = hasAnyDot1xOn && hasAnyDot1xOff;
  if (defaults.is_mixed_dot1x) {
    defaults.is_dot1x = false;
  }
  if (defaults.is_all_uplink) {
    defaults.port_mode = "trunk";
  }
  if (defaults.is_dot1x === true) {
    defaults.configured_speed = null;
    defaults.poe_config = null;
  }
  if (defaults.tagged_vlans) {
    const vlanIds = String(defaults.tagged_vlans).split(',').map(s => s.trim()).filter(id => id);
    const fullLabels = vlanIds.map(vlanId => {
      const vlan = vlanOptions.find(opt => String(opt.vlan_id) === vlanId);
      return vlan ? `${vlanId} - ${vlan.name}` : null;
    }).filter(label => label !== null);
    defaults.tagged_vlans = fullLabels;
  } else {
    defaults.tagged_vlans = [];
  }
  if (defaults.access_vlan) {
    const vlanId = String(defaults.access_vlan);
    const vlan = vlanOptions.find(opt => String(opt.vlan_id) === vlanId);
    if (vlan) {
      defaults.access_vlan = `${vlanId} - ${vlan.name}`;
    } else {
      defaults.access_vlan = null;
    }
  }
  let stpIdentical = true;
  const stpFields = ['bpdu_guard', 'bpdu_protection', 'root_guard', 'loop_guard'];
  for (const stpField of stpFields) {
    if (defaults[stpField] === null) {
      stpIdentical = false;
      break;
    }
  }
  if (stpIdentical) {
    const selectedStpModes = [];
    if (defaults.bpdu_guard === true) selectedStpModes.push('bpdu_guard');
    if (defaults.bpdu_protection === true) selectedStpModes.push('bpdu_protection');
    if (defaults.root_guard === true) selectedStpModes.push('root_guard');
    if (defaults.loop_guard === true) selectedStpModes.push('loop_guard');
    defaults.spanning_tree_mode = selectedStpModes;
  } else {
    defaults.spanning_tree_mode = [];
  }
  defaults.poe_status = defaults.poe_config;
  delete defaults.poe_config;
  return defaults;
}

