  const newRule = {
    id: Date.now().toString(),
    rule_name: "New Rule (Draft)",
    condition_field: "",
    condition_value: "",
    action_field: "",
    action_value: "",
    target_switches: [],
    status: "draft"
  };
  const updatedRules = [newRule].concat(currentRules);
  await storeValue('rulesList', updatedRules);
  await storeValue('_refresh', Date.now());
}

actionConfiguration.body:
async ruleId => {
  const allRules = appsmith.store.rulesList || [];
  const currentRuleData = allRules.find(r => r.id === ruleId);
  if (!currentRuleData) {
    showAlert("Error: Could not find rule data in store.", "error");
    return;
  }
  if (!currentRuleData.rule_name || currentRuleData.rule_name.trim() === "") {
    showAlert("Please enter a rule name", "error");
    return;
  }
  if (!currentRuleData.condition_field) {
    showAlert("Please select a condition field", "error");
    return;
  }
  if (!currentRuleData.condition_value) {
    showAlert("Please enter a condition value", "error");
    return;
  }
  if (!currentRuleData.action_field) {
    showAlert("Please select an action field", "error");
    return;
  }
  if (!currentRuleData.action_value) {
    showAlert("Please enter an action value", "error");
    return;
  }
  if (!currentRuleData.target_switches || currentRuleData.target_switches.length === 0) {
    showAlert("Please select target switches", "error");
    return;
  }
  RuleManager.latestRuleToSave = currentRuleData;
  try {
    await CreateRuleAPI.run();
    showAlert("Rule saved successfully!", "success");
    await GetRulesAPI.run();
    const apiRules = GetRulesAPI.data?.rules || [];
    await storeValue('rulesList', apiRules);
    await storeValue('_refresh', Date.now());
  } catch (error) {
    showAlert(`Failed to save rule: ${error.message}`, "error");
  }
}

