actionConfiguration.body:
async function () {
  try {
    showAlert("Applying configuration...", "info");
    const response = await globalConfig.run();
    GlobalConfigManager.lastResult = response;
    if (response.ok) {
      const s = response.statistics;
      showAlert(` Complete! Total: ${s.total} | Success: ${s.successful} | Failed: ${s.failed}`, "success");
      if (s.failed > 0) showModal('ResultsModal');
    } else {
      showAlert(" " + (response.message || "Failed"), "error");
    }
    return response;
  } catch (error) {
    showAlert(" Error: " + error.message, "error");
    return null;
  }
}

