export default {
  autoRefreshIntervalId: null,

  startAutoRefresh: function() {
    // Set the auto-refresh interval if the switch is on
    if (this.autoRefreshIntervalId === null) {
      this.autoRefreshIntervalId = setInterval(() => {
        getAllSwitches.run();
				getOfflineSwitches.run();
				getOnlineSwitches.run();
				getUnmonitoredSwitches.run();
      }, 10000); // 10000 ms = 10 seconds
    }
  },

  stopAutoRefresh: function() {
    // Clear the auto-refresh interval if it's running
    if (this.autoRefreshIntervalId !== null) {
      clearInterval(this.autoRefreshIntervalId);
      this.autoRefreshIntervalId = null;
    }
  },

  handleAutoRefreshToggle: function() {
    if (Switch1.isSwitchedOn) {
      // Start auto-refresh when the switch is turned on
      this.startAutoRefresh();
    } else {
      // Stop auto-refresh when the switch is turned off
      this.stopAutoRefresh();
    }
  }
}

