export default {
  /**
   * Retrieves the complete VLAN object from getVlans.data based on the selected VLAN ID
   * in the EditVlanDropdown. Returns a default object if no match is found or nothing
   * is selected, preventing runtime errors in form bindings.
   *
   * @returns {object} The selected VLAN object or a default placeholder.
   */
  getSelectedVlan: () => {
    // Assuming the main VLAN selection dropdown is named 'EditVlanDropdown'
    const selectedVlanId = EditVlanDropdown.selectedOptionValue;

    // Default structure for safety if no VLAN is selected
    const defaultVlan = {
      name: "",
      is_voice: 0,
      dhcp_snooping: 0,
      igmp_enable: 0,
      switch_count: 0
    };

    // If no VLAN is selected, return the default structure immediately
    if (!selectedVlanId) {
      return defaultVlan;
    }

    // Find the VLAN object in the query data
    // Use .toString() on both sides to handle cases where vlan_id might be a number 
    // in the data and a string in the dropdown value (or vice versa).
    const selectedVlan = getVlans.data.find(v => 
      v.vlan_id.toString() === selectedVlanId.toString()
    );

    // Return the found object or the safe default structure
    return selectedVlan || defaultVlan;
  }
}

