export default {

    // 1. CRITICAL VARIABLE for API payload binding (Step 1 of final fix)
    // The CreateRuleAPI body MUST be bound to RuleManagerJS.latestRuleToSave.<field_name>
    latestRuleToSave: {}, 

    // ===============================================
    //           AUTHENTICATION & SECURITY
    // ===============================================

    handleLogin: async (token) => {
        // AWAIT store operations to prevent the login loop
        await storeValue('authToken', token);
        await storeValue('isLoggedIn', true);

        // Navigate only AFTER the store is updated
        navigateTo('Tools', {}, 'SAME_WINDOW'); 
    },

    checkAuth: () => {
        // Runs on secured page load to check login status
        if (!appsmith.store.isLoggedIn) {
            showAlert('Session expired or login required.', 'error');
            navigateTo('LoginPage', {}, 'SAME_WINDOW');
        }
    },

    handleLogout: async () => {
        await storeValue('authToken', null);
        await storeValue('isLoggedIn', false);
        navigateTo('LoginPage', {}, 'SAME_WINDOW');
    },

    // ===============================================
    //            RULES INITIALIZATION & MANAGEMENT
    // ===============================================

    initializeRules: async () => {
        // Fetches initial data and populates the store
        try {
            const apiResponse = await GetRulesAPI.run(); 
            const rules = apiResponse.rules || []; 

            // Use the consistent store key: 'rulesList'
            await storeValue('rulesList', rules);
        } catch (e) {
            showAlert("Failed to load rules: " + e.message, "error");
        }
    },

    addNewRule: async () => {
        // Adds a new draft rule to the beginning of the list
        let currentRules = appsmith.store.rulesList || []; 
        const newRule = { 
            // Use a string ID for drafts
            id: Date.now().toString(), 
            rule_name: "New Rule (Draft)", // Non-empty default name for initial validation pass
            condition_field: "",
            condition_value: "",
            action_field: "",
            action_value: "",
            target_switches: [],
            status: "draft" 
        };

        const updatedRules = [newRule].concat(currentRules);

        await storeValue('rulesList', updatedRules); 
        await storeValue('_refresh', Date.now());
    },

    updateRuleField: async (ruleId, fieldName, value) => {
        // Two-way binding handler: updates the store as the user types (CRITICAL for deployed mode)
        let currentRules = appsmith.store.rulesList || [];

        const ruleIndex = currentRules.findIndex(r => r.id === ruleId);

        if (ruleIndex !== -1) {
            // Immutable update pattern
            const updatedRules = [...currentRules];
            const updatedRule = { ...updatedRules[ruleIndex] };

            // Set the new value for the specific field name
            updatedRule[fieldName] = value;
            updatedRules[ruleIndex] = updatedRule;

            await storeValue('rulesList', updatedRules);
        }
    },

    removeDraftRule: async (ruleId, ruleStatus) => {
        // Only allows deletion of rules marked as 'draft'
        if (ruleStatus !== 'draft') { 
            showAlert("Cannot remove saved rules this way.", "error");
            return;
        }

        const allRules = appsmith.store.rulesList || [];
        const filteredRules = allRules.filter(r => r.id !== ruleId);

        await storeValue('rulesList', filteredRules);
        await storeValue('_refresh', Date.now()); 

        showAlert("Draft removed", "info");
    },

    saveRule: async (ruleId) => {
        // --- 1. GET THE LATEST DATA FROM THE STORE ---
        // We bypass the widget's local state and read the absolute truth from the store.
        const allRules = appsmith.store.rulesList || [];
        const currentRuleData = allRules.find(r => r.id === ruleId);

        if (!currentRuleData) {
            showAlert("Error: Could not find rule data in store.", "error");
            return;
        }

        // --- 2. VALIDATION: USE STORE DATA FOR ALL CHECKS ---
        // This is the definitive fix for the "Please enter a rule name" error in deployed mode.

        if (!currentRuleData.rule_name || currentRuleData.rule_name.trim() === "") {
            showAlert("Please enter a rule name", "error");
            return;
        }
        if (!currentRuleData.condition_field) {
            showAlert("Please select a condition field", "error");
            return;
        }
        if (!currentRuleData.condition_value) {
            showAlert("Please enter a condition value", "error");
            return;
        }
        if (!currentRuleData.action_field) {
            showAlert("Please select an action field", "error");
            return;
        }
        if (!currentRuleData.action_value) {
            showAlert("Please enter an action value", "error");
            return;
        }
        if (!currentRuleData.target_switches || currentRuleData.target_switches.length === 0) {
            showAlert("Please select target switches", "error");
            return;
        }

        // --- 3. FORCE DATA INTO JS VARIABLE FOR API READ ---
        // Set the temporary variable so the API can read the correct, validated payload.
        RuleManager.latestRuleToSave = currentRuleData;

        // --- 4. API EXECUTION & REFRESH ---
        try {
            // CreateRuleAPI must be configured to read from RuleManagerJS.latestRuleToSave
            await CreateRuleAPI.run(); 

            showAlert("Rule saved successfully!", "success");

            // Refresh from database (AWAIT is required)
            await GetRulesAPI.run();

            // Update the store with the fresh data
            const apiRules = GetRulesAPI.data?.rules || [];
            await storeValue('rulesList', apiRules);
            await storeValue('_refresh', Date.now());

        } catch (error) {
            showAlert(`Failed to save rule: ${error.message}`, "error");
        }
    }
}

