export default {
/**
 * Utility functions for bulk editing records.
 *
 * @param {Array<Object>|Object} selectedRows - The array of selected rows (or a single object for single selection).
 * @param {Array<Object>} [vlanOptions=[]] - The full list of VLAN data objects (e.g., from getVlans.data), needed for label lookup.
 * @returns {Object} An object containing the common default values for the modal inputs.
 */

    calculateDefaults: (selectedRows, vlanOptions = []) => {
		// 1. Normalize input: Ensure selectedRows is an array.
		let rows = Array.isArray(selectedRows) ? selectedRows : (selectedRows ? [selectedRows] : []);

		if (rows.length === 0) {
			return {};
		}

		// List of fields to check for identity
		const fields = [
			'description',
			'port_mode',
			'access_vlan', // Handled differently in Step 3
			'tagged_vlans', // Handled differently in Step 2
			'admin_state',
			'configured_speed',
			'port_duplex',
			'poe_config', // <-- CHANGED: Using poe_config for bulk edit default
			'is_dot1x', // Boolean field (0 or 1)
			'is_uplink', // Used for identity check, but we need separate logic for 'any' uplink
			// Spanning Tree (STP) fields are handled together in Step 4
			'bpdu_guard',
			'bpdu_protection',
			'root_guard',
			'loop_guard'
		];

		const defaults = {};
		const rowCount = rows.length;

		/**
		 * Helper function to handle special data type conversions.
		 * Ensures consistent type (string/number/boolean) for comparison and returns the final required format.
		 * @param {string} key The field key.
		 * @param {*} value The raw value from the row.
		 * @returns {*} The processed value.
		 */
		const processValue = (key, value) => {
			if (value === null || value === undefined) {
				return null;
			}

			// For boolean/numeric fields, ensure we are comparing numbers/booleans consistently
			if (['is_dot1x', 'bpdu_guard', 'bpdu_protection', 'root_guard', 'loop_guard', 'is_uplink'].includes(key)) {
				// Convert to number for strict comparison
				const numericValue = Number(value);
				// Returns true/false for internal logic, 0/1 for comparison
				return numericValue === 1;
			}

			// Default: convert to string for consistency with Appsmith input widgets
			return String(value);
