			if (key === 'is_uplink' && firstValue === true) {
				hasAnyUplink = true;
			}
			if (key === 'is_dot1x') {
				if (firstValue === true) {
					hasAnyDot1xOn = true;
				} else {
					hasAnyDot1xOff = true;
				}
			}


			// Check all subsequent rows against the first one
			for (let i = 1; i < rowCount; i++) {
				const currentValue = (key === 'tagged_vlans') ? rows[i][key] : processValue(key, rows[i][key]);

				// Check subsequent rows for flags
				if (key === 'is_uplink' && currentValue === true) {
					hasAnyUplink = true;
				}
				if (key === 'is_dot1x') {
					if (currentValue === true) {
						hasAnyDot1xOn = true;
					} else {
						hasAnyDot1xOff = true;
					}
				}

				if (currentValue !== firstValue) {
					isIdentical = false;
				}
				portIds.push(rows[i].port_id);
			}

			// If all values are identical, set it as the default, otherwise set it to null (to show placeholder)
			defaults[key] = isIdentical ? commonValue : null;
		}

		// Ensure port_ids is unique for the title bar display
		defaults.port_ids = Array.from(new Set(portIds)).join(', ');

		// --- CONTROL LOGIC ---
		defaults.is_all_uplink = defaults.is_uplink === true; 
