export default {
    // Stores the original data
    initialVlan: {}, 

    // 1. Action to set the initial data (Called on query success)
    setInitialVlanData: (data) => {
        // IMPORTANT: We force the initial values to be NUMBERS (1 or 0)
        this.initialVlan = {
            name: data.name,
            is_voice: Number(data.is_voice),
            dhcp_snooping: Number(data.dhcp_snooping),
            igmp_enable: Number(data.igmp_enable),
        };
        console.log("--- VLAN UTILS DEBUG: Initial Data Set ---");
        console.log(this.initialVlan);
    },

    /**
     * Compares the current form data against the initial data, ensuring type consistency.
     * @returns {boolean} True if a change is detected, false otherwise.
     */
    hasChanges: (currentFormData) => {
        const initial = this.initialVlan;
        const current = currentFormData;

        if (Object.keys(initial).length === 0) {
            console.log("--- VLAN UTILS DEBUG: Not Ready, No Initial Data ---");
            return false;
        }

        console.log("--- VLAN UTILS DEBUG: Comparison Start ---");
        console.log("Initial State:", initial);
        console.log("Current State:", current);
        console.log("-----------------------------------------");

        // 1. Check text input
        if (initial.name !== current.name) {
            console.log("Change detected in name.");
            return true;
        }

        // 2. Check 0/1 checkbox fields
        if (initial.is_voice !== current.is_voice) {
            console.log("Change detected in is_voice.");
            return true;
        }

        if (initial.dhcp_snooping !== current.dhcp_snooping) {
            console.log("Change detected in dhcp_snooping.");
            return true;
        }

        if (initial.igmp_enable !== current.igmp_enable) {
            console.log("Change detected in igmp_enable.");
            return true;
        }

        // No changes found
        return false;
    }
}

