export default {
  handleAllSelection: async (newlySelectedValues) => { // Appsmith passes the new values to the handler
    const allValue = 'all';

    // Check if the 'all' option is among the new selection
    if (newlySelectedValues.includes(allValue)) {
      // If 'all' is selected, force *only* 'all' to be the final selection
      if (newlySelectedValues.length > 1) {
         await ruleSwitchApply.setSelectedOptions([allValue]);
      }
    } else {
      // If 'all' is NOT selected in the new values list, do nothing. 
      // The user chose specific items, which is valid. The UI already has the correct state.
    }
  }
}

