export default {
  lastResult: null,

  async applyConfig() {
    try {
      showAlert("Applying configuration...", "info");
      const response = await globalConfig.run();
      this.lastResult = response;

      if (response.ok) {
        const s = response.statistics;
        showAlert(` Complete! Total: ${s.total} | Success: ${s.successful} | Failed: ${s.failed}`, "success");
        if (s.failed > 0) showModal('ResultsModal');
      } else {
        showAlert(" " + (response.message || "Failed"), "error");
      }
      return response;
    } catch (error) {
      showAlert(" Error: " + error.message, "error");
      return null;
    }
  },

  getSuccessSwitches() {
    return this.lastResult?.results?.filter(r => r.success).map(r => r.switch_ip) || [];
  },

  getFailedSwitches() {
    return this.lastResult?.results?.filter(r => !r.success).map(r => ({
      ip: r.switch_ip,
      error: r.error || "Unknown",
      sections: r.sections || {}
    })) || [];
  },

  getSummary() {
    if (!this.lastResult?.statistics) return {total: 0, successful: 0, failed: 0};
    return this.lastResult.statistics;
  }
}

