export default {

    // 1. Handles the successful saving of the token and navigation.
    handleLogin: async (token) => {
        // CRITICAL FIX: AWAIT the storeValue operations to complete
        // Execution pauses here until the token is confirmed saved.
        await storeValue('authToken', token);
        await storeValue('isLoggedIn', true);

        // Only navigate AFTER the store is confirmed updated to prevent the login loop.
        navigateTo('Tools', {}, 'SAME_WINDOW'); 
    },

    // 2. The security gate run on every secured page load.
    checkAuth: () => {
        // Checks for the existence of the secure flag.
        if (!appsmith.store.isLoggedIn) {
            showAlert('Session expired or login required.', 'error');
            navigateTo('LoginPage', {}, 'SAME_WINDOW');
        }
    },

    // 3. Handles clearing the session and logging out.
    handleLogout: async () => {
        // Clear the stored values
        await storeValue('authToken', null);
        await storeValue('isLoggedIn', false);

        // Navigate to the login page
        navigateTo('LoginPage', {}, 'SAME_WINDOW');
    }
}

